def fetch_nba_data(url, headers):
    response = requests.get(url, headers=headers)
    data = response.json()
    return data

def process_data(data):
    headers = data['resultSets'][0]['headers']
    rows = data['resultSets'][0]['rowSet']
    df = pd.DataFrame(rows, columns=headers)
    return df

